mapper.select:
SELECT COUNT(USER_ID) AS COUNT
FROM JOSD.CHANTING
WHERE USER_ID = #{user_id}
AND   REC_DT  = #{rec_dt}

SELECT COUNT(USER_ID) AS COUNT
FROM JOSD.CALCULATION
WHERE USER_ID = #{user_id}
AND   REC_DT  = #{rec_dt}

mapper.update:
UPDATE JOSD.CALCULATION
SET CHANT_POINT = #{point_total},
	UP_DTIME = CONVERT(VARCHAR(10), getdate(), 102)
WHERE USER_ID = #{user_id}
AND   REC_DT  = #{rec_dt}
