mapper.select:
SELECT COUNT(USER_ID) AS COUNT
FROM JOSD.READINGHEARING
WHERE USER_ID = #{user_id}
AND   REC_DT  = #{rec_dt}
AND   SUB_AREA = #{sub_area}

mapper.update:
UPDATE JOSD.READINGHEARING
SET SUB_NAME = #{sub_name}, 
	SUB_DURA = #{sub_dura}, 
	SUB_POINT = #{sub_point}, 
	UP_DTIME = CONVERT(VARCHAR(10), getdate(), 102), 
	UP_ID	 = #{user_id}
WHERE USER_ID = #{user_id}
AND   REC_DT  = #{rec_dt}
AND   SUB_AREA = #{sub_area}
